import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import Box from '../components/Box'

export default class Login extends Component {
    state = {
        username: '',
        password: '',
    }

    onChange = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }

    render() {
        return (
            <div className="login">
                <Box extraClass="login-box">
                    <h1 style={{
                        margin: '20px',
                        marginTop: '5px',
                        fontSize: '40px',
                        // fontWeight: 'normal',
                        // fontFamily: 'Inter, Oxygen, sans-serif'
                    }}>IUBConnect</h1>
                    <form>
                        <input className="login-field" name="username" placeholder="Username" value={this.state.username} onChange={this.onChange} type="text"></input>
                        <input className="login-field" name="password" placeholder="Password" value={this.state.password} onChange={this.onChange} type="password"></input>
                        <Link to="/">
                            <button className="login-button">Log In</button>
                        </Link>
                    </form>
                    <div style={{ marginTop: "15px" }}>Don't have an account? Create here.</div>
                </Box>
            </div>
        )
    }
}
