import React, { Component } from 'react'
import Navbar from './components/Navbar'
import Sidebar from './components/Sidebar'
import Login from './pages/Login'
import Logout from './pages/Logout'
import Dashboard from './pages/Dashboard'
import Profile from './pages/Profile'
import Connections from './pages/Connections'
import { BrowserRouter, Switch, Route, Link } from 'react-router-dom'
import { Provider } from 'react-redux'
import store from './store'
import PrivateRoute from './components/PrivateRoute'
import { loadUser } from './actions/auth'
import Box from './components/Box'

export default class App extends Component {
    componentDidMount() {
        store.dispatch(loadUser())
    }

    render() {
        return (
            <Provider store={store}>
                <BrowserRouter>
                    {/* <Navbar app={this} />
                <Sidebar app={this} />
                <div className={this.state.sidebar ? "main-body" : "main-body main-body-sidebar"}>
                    <Switch>
                        <Route path="/" exact component={Dashboard} />
                        <Route path="/profile" exact component={Profile} />
                        <Route path="/connections" exact component={Connections} />
                    </Switch>
                </div> */}
                    <Switch>
                        <Route exact path="/login" component={Login} />
                        <Route exact path="/logout" component={Logout} />
                        <PrivateRoute component={MainView} />
                    </Switch>
                </BrowserRouter>
            </Provider>
        )
    }
}

class MainView extends Component {
    state = {
        sidebar: true,
        dropdown: false,
    }

    componentDidMount() {
        if (window.innerWidth <= 850) {
            this.setState({ sidebar: false })
        } else {
            this.setState({ sidebar: true })
        }
    }

    componentDidUpdate() {
        window.addEventListener('resize', () => {
            if (window.innerWidth <= 850) {
                this.setState({ sidebar: false })
            } else {
                this.setState({ sidebar: true })
            }
        })
    }

    render() {
        return (
            <>
                <Navbar app={this} />
                <Sidebar app={this} />
                <div className={this.state.sidebar ? "main-body" : "main-body main-body-sidebar"}>
                    <Switch>
                        <Route exact path="/" component={Dashboard} />
                        <Route exact path="/profile" component={Profile} />
                        <Route exact path="/connections" component={Connections} />
                        <Route component={() => <PageNotFound app={this} />} />
                    </Switch>
                </div>
            </>
        )
    }
}


class PageNotFound extends Component {
    // onClick = () => {
    //     this.props.app.setState({ sidebar: true })
    // }

    // componentWillMount() {
    //     this.props.app.setState({ sidebar: false })
    // }

    render() {
        return (
            <div style={{ position: 'absolute' }}>
                <div className="login">
                    {/* <Box extraClass="main-box"> */}
                    <div style={{ textAlign: 'center' }}>
                        <h1>Page Not Found</h1>
                        <p>Go back to <Link onClick={this.onClick} to="/" className="inline-link">homepage</Link>.</p>
                    </div>
                    {/* </Box> */}
                </div>
            </div>
        )
    }
}
