import React, { Component } from 'react'
import { NavLink } from 'react-router-dom'
import { FaHome, FaUserFriends } from 'react-icons/fa'
import { BsPeopleCircle } from 'react-icons/bs'

export default class Sidebar extends Component {
    componentDidMount() {
        window.addEventListener('resize', () => {
            if (window.innerWidth <= 850) {
                this.props.app.setState({ sidebar: false })
            } else {
                this.props.app.setState({ sidebar: true })
            }
        })
    }

    componentDidUpdate() {
        window.addEventListener('resize', () => {
            if (window.innerWidth <= 850) {
                this.props.app.setState({ sidebar: false })
            } else {
                this.props.app.setState({ sidebar: true })
            }
        })
    }

    render() {
        return (
            <div className={this.props.app.state.sidebar ? "sidebar" : "sidebar sidebar-hidden"}>
                <ul className="sidebar-items">
                    <NavLink to="/" activeClassName="active-hightlight"><li><FaHome className="sidebar-icon" />Dashboard</li></NavLink>
                    <NavLink to="/profile" activeClassName="active-hightlight"><li><BsPeopleCircle className="sidebar-icon" />Profile</li></NavLink>
                    <NavLink to="/connections" activeClassName="active-hightlight"><li><FaUserFriends className="sidebar-icon" />Connections</li></NavLink>
                </ul>
            </div>
        )
    }
}
